// Example: pages/some-page.js (client side)
useEffect(() => {
  fetch('/api/secure-data')
    .then(res => res.json())
    .then(data => console.log(data));
}, []);

// pages/secure.js
export async function getServerSideProps(context) {
  const cookies = context.req.headers.cookie || '';
  console.log('Cookies from client:', cookies);

  // Example: forward the cookie to a backend API
  const res = await fetch('https://your-api.com/user', {
    headers: {
      Cookie: cookies, // pass cookies to your backend
    },
  });

  const data = await res.json();

  return {
    props: { user: data },
  };
}

// pages/api/profile.js
export default function handler(req, res) {
  const cookies = req.headers.cookie; // e.g., "token=abc123; session=xyz"
  console.log('Received Cookies:', cookies);

  res.status(200).json({ message: 'Success' });
}

