public class SimpleProgram {
    public static void main(String[] args) {
        // Print Hello World
        System.out.println("Hello, World!");

        // Declare two numbers
        int num1 = 10;
        int num2 = 20;

        // Calculate sum
        int sum = num1 + num2;

        // Print result
        System.out.println("Sum of " + num1 + " and " + num2 + " is: " + sum);
    }
}
