attrs.content:
 editMilestone(milestone: CampaignMilestone) {
    if (!this.canEdit) return;
    if (this.editTimelineComponent || this.isRecap || this.IsDownload) return;
    if (!!milestone.items && milestone.items.length > 0) return;

    const modalRef = this.modalService.open(MilestoneStatusEditorComponent, {
      size: TIMELINE_BLOCKS_CONSTANTS.UI.MODAL.SIZE,
      modalDialogClass: TIMELINE_BLOCKS_CONSTANTS.UI.MODAL.CLASS,
    });
    modalRef.componentInstance.milestone = milestone;
    modalRef.componentInstance.campaign = this.timeLineData;
    modalRef.result.then(() => {
      this.handleTimeline().then(() => { });
    }).catch(() => { });
  }

  @HostBinding('class')
  get classes(): Record<string, boolean> {
    return {
      'horizontal-timeline-parent': this.displayMode == TimelineDisplayMode.Horizontal,
      'horizontal-multiline-parent': this.displayMode == TimelineDisplayMode.HorizontalMultiLine,
      'vertical-list-parent': this.displayMode == TimelineDisplayMode.VerticalList,
    };
  }
}
