-- code by cursor -- final export function useCollaborativeEditor({ editor, editorKey, mode, onSetLeader, isRestrictedPage, noteType }: Props) {
  // ... existing state ...
  const timeoutIdRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (!editor || !editorKey || noteType !== "original") return;

    let ydoc: Y.Doc | null = null;
    let provider: WebsocketProvider | null = null;
    let awareness: Awareness | null = null;

    try {
      // Create Y.js document
      ydoc = new Y.Doc();

      // Apply offline backup if any
      const localBackup = localStorage.getItem(`novel-yjs-${editorKey}`);
      if (localBackup) {
        try {
          const update = JSON.parse(localBackup);
          if (update) {
            Y.applyUpdate(ydoc, new Uint8Array(update));
          }
        } catch (err) {
          console.error("Failed to apply local Yjs backup:", err);
        }
      }

      // Create WebSocket provider
      provider = new WebsocketProvider(process.env.SOCKET_SERVER_URL as string, editorKey, ydoc);
      awareness = provider.awareness;

      // Set up awareness
      awareness.setLocalStateField("user", {
        name: email,
        color: randomColor,
        isRestrictedPage: isRestrictedPage
      });

      // Event handlers
      const handleStatus = (event: { status: string }) => {
        setSocketConnected(event.status === "connected");
      };

      const handleSync = (isSynced: boolean) => {
        if (isSynced && !syncedOnceRef.current) {
          syncedOnceRef.current = true;
          setInitialSyncDone(true);
        }
      };

      const handleUpdate = (update: Uint8Array) => {
        localStorage.setItem(`novel-yjs-${editorKey}`, JSON.stringify(Array.from(update)));
      };

      const checkLeadership = () => {
        // Leadership detection logic
        if (awareness && ydoc) {
          const localClientID = ydoc.clientID;
          // ... leadership logic ...
        }
      };

      // Register event listeners
      provider.on("status", handleStatus);
      provider.on("sync", handleSync);
      ydoc.on("update", handleUpdate);
      awareness.on("update", checkLeadership);

      // Register editor plugins (Tiptap handles cleanup)
      const yXmlFragment = ydoc.getXmlFragment("prosemirror");
      editor.registerPlugin(ySyncPlugin(yXmlFragment));
      editor.registerPlugin(yCursorPlugin(awareness));
      editor.registerPlugin(yUndoPlugin());

      // Set timeout for sync fallback
      timeoutIdRef.current = setTimeout(() => {
        if (!syncedOnceRef.current) {
          console.warn("‚ö†Ô∏è Sync event missed, forcing initialSyncDone = true");
          syncedOnceRef.current = true;
          setInitialSyncDone(true);
        }
      }, 1500);

    } catch (error) {
      console.error("Failed to initialize collaborative editor:", error);
    }

    // Cleanup function - CORRECTED VERSION
    return () => {
      try {
        // Clear timeout if still pending
        if (timeoutIdRef.current) {
          clearTimeout(timeoutIdRef.current);
          timeoutIdRef.current = null;
        }

        // Remove event listeners
        if (provider) {
          provider.off("status", handleStatus);
          provider.off("sync", handleSync);
        }
        if (awareness) {
          awareness.off("update", checkLeadership);
          awareness.setLocalState(null); // CRITICAL: Clear user presence
        }
        if (ydoc) {
          ydoc.off("update", handleUpdate);
        }

        // DON'T try to unregister Tiptap plugins - let Tiptap handle it

        // Destroy connections
        if (provider) {
          provider.destroy(); // Better than disconnect()
        }
        if (ydoc) {
          ydoc.destroy();
        }

        // Reset state
        setSocketConnected(false);
        setInitialSyncDone(false);
        syncedOnceRef.current = false;

      } catch (error) {
        console.error("Error during cleanup:", error);
      }
    };
  }, [editor, editorKey, noteType, email, randomColor, isRestrictedPage]);
}
