httpGet<T>(
	res: string,
	analyticsApi: boolean = false,
	mockapi: boolean = false,
	customHeaders: any = {},
	options: ApiRequestOptions = {}
): Observable<T> {
	let headers = HelperService.getHttpHeaders_httpClient(mockapi, customHeaders);

	let baseUrl = mockapi
		? environment.api.mockapiUrl
		: analyticsApi
			? environment.api.analyticsUrl
			: environment.api.baseUrl;

	let endpointUrl = baseUrl + res;

	let requestOptions: any = {
		headers,
		withCredentials: true,
		responseType: options.blobResponse ? 'blob' : 'json'
	};

	return this.httpClient.get<T>(endpointUrl, requestOptions).pipe(
		catchError((error: HttpErrorResponse) => {
			this.healthCheck();

			if (res.endsWith('api/dashboards') || res.endsWith('api/selfTrainer')) {
				return of({ menus: [] } as T);
			}

			return this.handleError({
				error,
				options,
				url: endpointUrl,
				method: 'GET'
			});
		})
	);
}

	/**
 * Create http headers object
 * @mockapi boolean If true, then dont add token since it fails
 * @returns {Headers}
 */
	static getHttpHeaders_httpClient(uploadFile: boolean = false, customHeaders: any = {}): HttpHeaders {
		let headers = new HttpHeaders();

		if (!uploadFile) {
			headers = headers.set('Accept', 'application/json').set('Content-Type', 'application/json');
		}

		Object.entries(customHeaders).forEach(([key, value]) => {
			headers = headers.set(key, String(value));
		});

		let token = localStorage.getItem('token') || localStorage.getItem('token_external');

		if (token) {
			headers = headers.set('Authorization', 'Bearer ' + token);
		} else {
			const registerData = JSON.parse(localStorage.getItem('register_data') || '{}');
			if (registerData?.auth?.token) {
				headers = headers.set('Authorization', 'Bearer ' + registerData.auth.token);
			}
		}

		return headers;
	}
