function reverseWordsInSentence(sentence) {
  return sentence
    .split(" ") // split by whitespace but preserve it
    .map(word => {
      // check if word has punctuation at the end (.,!?)
      const match = word.match(/^(\w+)([.,!?])$/);
      if (match) {
        // reverse the word part, keep the punctuation at end
        return match[1].split('').reverse().join('') + match[2];
      } else {
        return word.split('').reverse().join('');
      }
    })
    .join('');
}

// Example usage
const input = "Hi Shyam, How are you? I am good.";
const output = reverseWordsInSentence(input);
console.log(output);
