content.text:
:root {
  --c-texPri: #1f2329;
  --c-texSec: #5f6a7a;
  --c-icoSec: #6b7280;
  --cd-homCarBacBas: #f7f8fb;
  --cd-homCarBor: rgba(196, 203, 214, 0.7);
  --cd-homCarSha: rgba(15, 23, 42, 0.18);
  --ca-homCarCovPhoBas: linear-gradient(135deg, rgba(203, 213, 225, 0.4), rgba(226, 232, 240, 0.2));
  --home-surface-bg: transparent;
  --home-surface-border: transparent;
  --home-surface-shadow: none;
  --home-edit-banner-bg: rgba(243, 244, 246, 0.9);
  --home-edit-banner-border: rgba(17, 24, 39, 0.18);
  --home-edit-banner-text: rgba(17, 24, 39, 0.8);
}

.dark {
  --c-texPri: #e5edf9;
  --c-texSec: #94a3b8;
  --c-icoSec: #94a3b8;
  --cd-homCarBacBas: rgba(22, 27, 37, 0.9);
  --cd-homCarBor: rgba(71, 85, 105, 0.65);
  --cd-homCarSha: rgba(15, 23, 42, 0.45);
  --ca-homCarCovPhoBas: linear-gradient(135deg, rgba(63, 63, 70, 0.48), rgba(39, 39, 42, 0.3));
  --home-surface-bg: transparent;
  --home-surface-border: transparent;
  --home-surface-shadow: none;
  --home-edit-banner-bg: rgba(30, 30, 40, 0.6);
  --home-edit-banner-border: rgba(203, 213, 225, 0.25);
  --home-edit-banner-text: rgba(229, 229, 229, 0.85);
}

.layout {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.layout.layout-home {
  min-height: calc(100vh - 56px);
  padding: clamp(32px, 6vw, 10px) clamp(16px, 5vw, 10px);
  display: flex;
  justify-content: flex-start;
  background: linear-gradient(180deg, #fafafa, #f1f3f5);
}

.dark .layout.layout-home {
  background: linear-gradient(180deg, #0f172a, #111827);
}

.home-hero-block {
  width: 100%;
  height: 100%;
  display: flex;
  position: relative;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  padding-top: 16px;
  gap: clamp(12px, 4vw, 28px);
}

.home-hero-primary,
.home-hero-secondary {
  width: 100%;
  margin-top: clamp(12px, 3vw, 32px);
  z-index: 1;
  padding-inline: clamp(24px, 4vw, 48px);
  pointer-events: none;
  transition: 0.5s ease-in-out 2.75s;
  display: flex;
  align-items: center;
  justify-content: flex-start;
}

.home-hero-secondary {
  opacity: 0;
  transform: translateY(-12px);
  position: absolute;
}

.home-hero-primary-inner,
.home-hero-secondary-inner {
  width: 100%;
  font-size: clamp(28px, 5vw, 36px);
  line-height: 1.2;
  font-weight: 600;
  color: var(--c-texPri);
  display: flex;
  align-items: center;
  justify-content: flex-start;
  text-align: left;
}

.home-hero-primary-inner span {
  display: flex;
  pointer-events: all;
  max-width: 100%;
}

.home-content {
  display: flex;
  flex-direction: column;
  gap: 28px;
  position: relative;
  flex: 1;
}

.home-surface {
  user-select: none;
  background: var(--home-surface-bg);
  border: none;
  border-radius: 0;
  box-shadow: var(--home-surface-shadow);
  backdrop-filter: none;
  position: relative;
  overflow: hidden;
}

.home-surface--full {
  width: 100%;
  max-width: none;
  margin: 0;
  padding: clamp(24px, 5vw, 10px);
  min-height: calc(100vh - clamp(64px, 12vw, 120px));
  display: flex;
  flex-direction: column;
  gap: clamp(24px, 5vw, 48px);
}

.home-toolbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 32px;
  padding-bottom: 8px;
  margin-inline-start: 0;
  flex-wrap: wrap;
  gap: 12px;
}

.home-toolbar-left {
  display: flex;
  align-items: center;
  font-size: 12px;
  font-weight: 500;
  color: var(--c-texSec);
  fill: var(--c-icoSec);
  gap: 8px;
}

.home-toolbar-left svg {
  width: 16px;
  height: 16px;
  display: block;
  fill: var(--c-icoSec);
}

.home-toolbar-actions {
  display: flex;
  align-items: center;
  gap: 10px;
}

.home-edit-banner {
  margin: 0 8px 18px;
  border-radius: 24px;
  border: 1px dashed var(--home-edit-banner-border);
  padding: 14px 18px;
  background: var(--home-edit-banner-bg);
  color: var(--home-edit-banner-text);
  font-size: 13px;
  font-weight: 500;
}

.home-canvas {
  position: relative;
  width: 100%;
  min-height: 280px;
  border-radius: 28px;
  border: 1px dashed transparent;
  padding: clamp(20px, 4vw, 32px);
  transition: border 0.3s ease, background 0.3s ease;
}

.home-canvas--editing {
  border-color: rgba(17, 17, 17, 0.35);
  background: rgba(17, 17, 17, 0.04);
}

.notion-scroller.horizontal {
  overflow: auto hidden;
  mask-image: linear-gradient(to right, transparent 0%, black 96px, black calc(100% - 96px), transparent 100%);
  -webkit-mask-image: linear-gradient(to right, transparent 0%, black 96px, black calc(100% - 96px), transparent 100%);
}

.hide-scrollbar::-webkit-scrollbar {
  display: none;
}

.hide-scrollbar {
  scrollbar-width: none;
}

.home-recent-row {
  display: flex;
  gap: 16px;
  width: max-content;
  margin-bottom: 1px;
  padding: 4px 0 24px;
}

.home-recent-card {
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: stretch;
  flex-shrink: 0;
  overflow: hidden;
  border-radius: 16px;
  background: var(--cd-homCarBacBas);
  border: 1px solid var(--cd-homCarBor);
  width: 144px;
  height: 144px;
  transition: transform 0.2s ease, box-shadow 0.2s ease, border 0.2s ease;
  text-decoration: none;
  color: inherit;
}

.home-recent-card:hover {
  transform: translateY(-3px);
  box-shadow: 0 18px 40px -28px var(--cd-homCarSha);
}

.home-recent-panel {
  position: relative;
  padding-right: 44px;
}

.home-recent-panel-actions {
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  align-items: center;
  gap: 6px;
}

.home-recent-card-cover-wrapper {
  position: relative;
  width: 100%;
  margin-bottom: 16px;
  height: 44px;
}

.home-recent-card-cover {
  height: 44px;
  background: var(--ca-homCarCovPhoBas);
  pointer-events: none;
}

.home-recent-card-icon {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  height: 28px;
  width: 28px;
  position: absolute;
  bottom: -14px;
  left: 16px;
  font-size: 26px;
}

.home-recent-card-body {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 12px;
  padding: 0 16px 16px;
  font-size: 13px;
  line-height: 1.35;
  color: var(--c-texPri);
}

.home-recent-card-time {
  font-size: 11px;
  color: var(--c-texSec);
}

.home-widget-empty {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  gap: 12px;
  font-size: 13px;
  color: var(--c-texSec);
  padding: 32px 0;
}

.home-widget-empty strong {
  color: var(--c-texPri);
  font-weight: 600;
}

.home-widget-error {
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  padding: 32px 0;
  color: rgb(220, 38, 38);
}

.home-widget {
  display: flex;
  flex-direction: column;
  height: 100%;
  border-radius: 28px;
  border: 1px solid rgba(148, 163, 184, 0.35);
  background: rgba(255, 255, 255, 0.88);
  padding: 24px;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.7), 0 24px 60px -36px rgba(15, 23, 42, 0.25);
  backdrop-filter: blur(16px);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.home-widget:hover {
  transform: translateY(-3px);
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.8), 0 32px 70px -42px rgba(15, 23, 42, 0.35);
}

.dark .home-widget {
  background: rgba(15, 23, 42, 0.82);
  border-color: rgba(71, 85, 105, 0.65);
  box-shadow: inset 0 1px 0 rgba(148, 163, 184, 0.12), 0 32px 70px -42px rgba(15, 23, 42, 0.9);
}

.home-widget header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
  margin-bottom: 20px;
}

.home-widget-title {
  display: flex;
  align-items: center;
  gap: 12px;
}

.home-widget-title .icon-wrapper {
  display: grid;
  place-items: center;
  width: 32px;
  height: 32px;
  border-radius: 999px;
  background: rgba(17, 24, 39, 0.08);
  color: rgba(17, 24, 39, 0.7);
}

.dark .home-widget-title .icon-wrapper {
  background: rgba(236, 236, 240, 0.12);
  color: rgba(226, 232, 240, 0.85);
}

.home-widget-title h2 {
  font-size: 15px;
  font-weight: 600;
  color: var(--c-texPri);
}

.home-widget-actions {
  display: flex;
  align-items: center;
  gap: 8px;
}

.home-drag-indicator {
  position: absolute;
  top: 18px;
  left: 22px;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  border-radius: 999px;
  background: rgba(248, 248, 248, 0.95);
  color: rgba(45, 55, 72, 0.85);
  font-size: 12px;
  font-weight: 500;
  box-shadow: 0 8px 18px -12px rgba(15, 23, 42, 0.35);
  pointer-events: auto;
}

.dark .home-drag-indicator {
  background: rgba(18, 24, 38, 0.92);
  color: rgba(203, 213, 225, 0.9);
  box-shadow: 0 12px 24px -16px rgba(12, 18, 32, 0.9);
}

.home-delete-button {
  position: absolute;
  top: 16px;
  right: 18px;
  height: 36px;
  width: 36px;
  border-radius: 999px;
  background: rgba(255, 255, 255, 0.9);
  color: rgba(71, 85, 105, 0.9);
  display: grid;
  place-items: center;
  border: none;
  cursor: pointer;
  box-shadow: 0 12px 32px -18px rgba(244, 67, 54, 0.45);
}

.home-delete-button:hover {
  background: rgba(254, 242, 242, 0.95);
  color: #dc2626;
}

.dark .home-delete-button {
  background: rgba(15, 23, 42, 0.92);
  color: rgba(203, 213, 225, 0.9);
  box-shadow: 0 12px 32px -18px rgba(248, 113, 113, 0.4);
}

.dark .home-delete-button:hover {
  background: rgba(127, 29, 29, 0.42);
  color: #fca5a5;
}

.home-resize-handle {
  position: absolute;
  right: 18px;
  bottom: 18px;
  width: 20px;
  height: 20px;
  border-radius: 6px;
  border: 1px solid rgba(148, 163, 184, 0.6);
  background: rgba(255, 255, 255, 0.92);
  box-shadow: 0 10px 24px -16px rgba(15, 23, 42, 0.35);
  cursor: se-resize;
}

.dark .home-resize-handle {
  background: rgba(15, 23, 42, 0.92);
  border-color: rgba(148, 163, 184, 0.65);
  box-shadow: 0 10px 24px -16px rgba(15, 23, 42, 0.9);
}

.time-widget {
  background: linear-gradient(180deg, #f8fafc, #eceff4);
  border: 1px solid rgba(148, 163, 184, 0.45);
  color: var(--c-texPri);
}

.dark .time-widget {
  background: linear-gradient(180deg, rgba(30, 41, 59, 0.94), rgba(15, 23, 42, 0.88));
  border-color: rgba(71, 85, 105, 0.65);
  color: #e2e8f0;
}

.time-widget-body {
  display: flex;
  flex-direction: column;
  gap: 14px;
  margin-top: 4px;
  flex: 1;
}

.time-widget-content {
  display: flex;
  flex: 1;
}

.time-widget-meta {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 11px;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: rgba(100, 116, 139, 0.8);
}

.dark .time-widget-meta {
  color: rgba(148, 163, 184, 0.8);
}

.time-widget-time {
  font-size: clamp(26px, 4vw, 34px);
  font-weight: 600;
  letter-spacing: -0.02em;
  color: inherit;
}

.time-widget-date {
  font-size: 12px;
  color: rgba(100, 116, 139, 0.9);
}

.dark .time-widget-date {
  color: rgba(148, 163, 184, 0.85);
}

.time-widget-change {
  font-size: 12px;
  font-weight: 500;
  color: rgba(59, 68, 85, 0.9);
}

.dark .time-widget-change {
  color: rgba(203, 213, 225, 0.85);
}

.time-widget-popover {
  width: 260px;
  padding: 14px;
  border-radius: 16px;
  background: rgba(255, 255, 255, 0.95);
  border: 1px solid rgba(148, 163, 184, 0.35);
  box-shadow: 0 22px 60px -32px rgba(15, 23, 42, 0.45);
}

.dark .time-widget-popover {
  background: rgba(15, 23, 42, 0.96);
  border-color: rgba(71, 85, 105, 0.65);
  box-shadow: 0 28px 70px -24px rgba(15, 23, 42, 0.9);
}

.time-widget-search {
  margin-bottom: 12px;
}

.time-widget-search input {
  width: 100%;
  border: 1px solid rgba(148, 163, 184, 0.6);
  border-radius: 10px;
  padding: 8px 10px;
  font-size: 12px;
  background: rgba(248, 250, 252, 0.85);
  color: inherit;
}

.dark .time-widget-search input {
  background: rgba(30, 41, 59, 0.72);
  border-color: rgba(71, 85, 105, 0.7);
}

.time-widget-options {
  display: flex;
  flex-direction: column;
  gap: 4px;
  max-height: 220px;
  overflow: auto;
}

.time-widget-option {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 12px;
  padding: 8px 10px;
  border-radius: 10px;
  font-size: 12px;
  color: inherit;
  background: transparent;
  border: 1px solid transparent;
  transition: background 0.2s ease, border-color 0.2s ease;
}

.time-widget-option:hover {
  background: rgba(241, 245, 249, 0.9);
  border-color: rgba(148, 163, 184, 0.45);
}

.dark .time-widget-option:hover {
  background: rgba(30, 41, 59, 0.75);
  border-color: rgba(71, 85, 105, 0.6);
}

.time-widget-option--active {
  background: rgba(148, 163, 184, 0.18);
  border-color: rgba(148, 163, 184, 0.6);
}

.dark .time-widget-option--active {
  background: rgba(59, 73, 105, 0.35);
  border-color: rgba(148, 163, 184, 0.65);
}

.time-widget-empty-result {
  padding: 20px 12px;
  text-align: center;
  font-size: 12px;
  color: rgba(100, 116, 139, 0.7);
}

.dark .time-widget-empty-result {
  color: rgba(148, 163, 184, 0.7);
}

@media (max-width: 768px) {
  .layout.layout-home {
    padding: 56px 20px 120px;
  }

  .home-hero-primary,
  .home-hero-secondary {
    padding-inline: 24px;
    margin-top: 32px;
  }

  .home-surface--full {
    padding: 36px 20px 48px;
    gap: 40px;
  }

  .home-recent-card {
    width: 132px;
    height: 132px;
  }

  .home-toolbar-actions {
    width: 100%;
    justify-content: flex-end;
  }
}
